	private static String loadScript(String scriptFile) {	
		
		try {
			
		  // add file to the editable script list 	
		  editableScriptList.add(scriptFile);
		  
		  return new String(Files.readAllBytes(Paths.get(scriptFile))); 
		} catch (IOException e) 
		{ return "Preload file not found: "+scriptFile; }

	}  

