    private List<Construct> loadConstructFromJSON(String jsonStr) {
		List<Construct> construct = new ArrayList<>();

		JSONParser parser = new JSONParser();
		try { 
			Object obj = parser.parse(jsonStr);
			JSONArray constrList = (JSONArray)obj;
			for(int i = 0; i < constrList.size(); i++) {
				double[] pos = new double[3];
				double[] size = new double[3];
				double[] speed = new double[3];
				JSONArray tmp;				
				JSONObject details = (JSONObject)constrList.get(i);	

				tmp = (JSONArray)details.get("pos");	
				pos[0] = (double)tmp.get(0); pos[1] = (double)tmp.get(1); pos[2] = (double)tmp.get(2);

				tmp = (JSONArray)details.get("size");	
				size[0] = (double)tmp.get(0); size[1] = (double)tmp.get(1); size[2] = (double)tmp.get(2);

				tmp = (JSONArray)details.get("speed");	
				speed[0] = (double)tmp.get(0); speed[1] = (double)tmp.get(1); speed[2] =(double) tmp.get(2);

				double mass = (double)((Object)details.get("mass")  );   
				
				construct.add(new Construct(i+1, pos, size, speed , (int)Math.floor((double) details.get("owner")), (String)details.get("name"), (String)details.get("ctype"), mass  ));  
			}	
			
		} catch(ParseException e){ 	
			
			e.printStackTrace();
	    }  
    	return construct;		

    }

    private List<Player> loadPlayerFromJSON(String jsonStr) {
	 	List<Player> player = new ArrayList<>();
		JSONParser parser = new JSONParser();
		try { 
			Object obj = parser.parse(jsonStr);
			JSONArray playerList = (JSONArray)obj;
			for(int i = 0; i < playerList.size(); i++) {
				double[] pos = new double[3];
				JSONArray tmp;				
				JSONObject details = (JSONObject)playerList.get(i);	

				int id = (int)Math.round((double)details.get("id"));

				tmp = (JSONArray)details.get("worldPos");					
				pos[0] = (double)tmp.get(0); 
				pos[1] = (double)tmp.get(1); 
				pos[2] = (double)tmp.get(2);				
			    
				player.add(new Player(id, (String)details.get("name"), pos));  
			}	

		} catch(ParseException e){ 	
			
			e.printStackTrace();
	    }  
		
    	return player;
    }
    
    // check if bounds overlap one of the existing elements
    private boolean isFree(int id, int x, int y, int sizeX, int sizeY, int margin) {
	    float stepx = sizeX/4;
	    float stepy = sizeY/4;
	    int eX, eY, eSizeX, eSizeY;
		
    	for (DUElement elem : elements ) {
		    if(elem == null || elem.id == id) continue;
//		    System.out.println("item "+elem.name+" id:"+elem.id);
    		eSizeX = elem.element.panel.getWidth()+margin;
    		eSizeY = elem.element.panel.getHeight()+margin;
		    eX = elem.element.x-margin;
		    eY = elem.element.y-margin;
		    
		    // check if one of the 16 points inside the bounds
		    // overlap this elements
		    for(float xx = x; xx <= x+sizeX; xx+=stepx) {
		    	for(float yy = y; yy <= y+sizeY; yy+=stepy) {		    				    		
                     
		    		// if(elem.id == 1) System.out.println("check for "+elem.name+" yy:"+yy+"  ey:"+eY+"  widht:"+eY+eSizeY);
		    		if(xx >= eX && xx <= eX+eSizeX && yy >= eY && yy <= eY+eSizeY) {
		    	       return false;    	
		    	    }		    		
		    		
		    	}
		    }
    	}
    	
    	return true;
    }
    
    // search the next free xy position (insert)
    private int[] findNextPos(int elementId) {
    	int[] pos = new int[2];

		Dimension screenSize = Toolkit.getDefaultToolkit().getScreenSize();
		
		//height of the task bar
		Insets taskBarSize = Toolkit.getDefaultToolkit().getScreenInsets(frame.getGraphicsConfiguration());
		
        // screenSize.width, screenSize.height
		int margin = 5;
		int x = margin, y = margin;
		
		// get element size 
		int sizeX = elements[elementId].element.panel.getWidth()+5;
		int sizeY = elements[elementId].element.panel.getHeight()+18;
		
		// find the next xy position
		while(x+sizeX+margin <= screenSize.width) {
			
			while(y+sizeY+margin <= (screenSize.height-taskBarSize.bottom-20)) {

				if(isFree(elementId, x, y, sizeX, sizeY, margin)) {
					pos[0] = x;
					pos[1] = y;
					return pos; 
				}
				
				y = y + margin;
			}	
			
			x = x + margin;
			y = margin;
		}    	
    	
		pos[0] = -1; pos[1] = -1;
    	return pos;
    }

    private void defaultPosition(int id) {
		 int[] pos = findNextPos(id);
		 elements[id].element.x = pos[0];
		 elements[id].element.y = pos[1];

		 // record as default position.
		 elements[id].X = 0; 
		 elements[id].Y = 0;
		 
		 elements[id].element.panel.setLocation(elements[id].element.x, elements[id].element.y);
    }
    
